import math

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

n = 2025
print(f'Is 2025 prime? {is_prime(n)}')

# Also find factors if not prime
factors = []
for i in range(2, int(math.sqrt(n)) + 1):
    if n % i == 0:
        factors.append(i)
        factors.append(n // i)
        break
print(f'Factors found: {factors}')
